StructuredTextModel.Text:
IF AlarmStructure.EventType.ID=0 THEN
	AlarmString:='';
ELSE
	AlarmStringInternal:=NumToDecString(AlarmStructure.EventType.ID,4,_ZERO);
	DINT_TO_STRING(AlarmStructure.EventType.ID);
	CASE AlarmStructure.EventType.Category OF
		0:		AlarmStringInternal:=concat(AlarmStringInternal,'    Error      ');
		1:		AlarmStringInternal:=concat(AlarmStringInternal,'    Alarm      ');
		2:		AlarmStringInternal:=concat(AlarmStringInternal,'    Warning    ');
		3:		AlarmStringInternal:=concat(AlarmStringInternal,'    Event      ');
	END_CASE;
	AlarmStringInternal:=CONCAT(AlarmStringInternal,AlarmStructure.EventType.Message);
	IF AlarmStructure.Active=FALSE THEN
		AlarmStringInternal:=concat(AlarmStringInternal,'     NOT Active');
	ELSE
		AlarmStringInternal:=concat(AlarmStringInternal,'     ACTIVE    ');
	END_IF;

	IF AlarmStructure.OccuredTime>AlarmStructure.AcknowledgedTime THEN
		DtToDateStruct(AlarmStructure.OccuredTime,Alarmdate);
	ELSE
		DtToDateStruct(AlarmStructure.AcknowledgedTime,Alarmdate);
	END_IF;
	myhour:=USINT_TO_STRING(alarmdate.Hour);
	myminute:=USINT_TO_STRING(alarmdate.Min);
	mysecond:=USINT_TO_STRING(alarmdate.Sec);
	AlarmStringInternal:=CONCAT(AlarmStringInternal,'     ');
	IF Alarmdate.Hour<10 THEN AlarmStringInternal:=CONCAT(AlarmStringInternal,'0'); END_IF;
	AlarmStringInternal:=CONCAT(AlarmStringInternal,myhour);
	AlarmStringInternal:=CONCAT(AlarmStringInternal,':');
	IF Alarmdate.Min<10 THEN AlarmStringInternal:=CONCAT(AlarmStringInternal,'0'); END_IF;
	AlarmStringInternal:=CONCAT(AlarmStringInternal,myminute);
	AlarmStringInternal:=CONCAT(AlarmStringInternal,':');
	IF Alarmdate.Sec<10 THEN AlarmStringInternal:=CONCAT(AlarmStringInternal,'0'); END_IF;
	AlarmStringInternal:=CONCAT(AlarmStringInternal,mysecond);
	AlarmString:=AlarmStringInternal;
END_IF;
